// Import required packages
const express = require("express");

// Create an Express app
const app = express();
const PORT = 3000;

// Middleware to parse JSON data
app.use(express.json());

// Serve frontend files from the "public" folder
app.use(express.static("public"));

// In-memory storage for inventory items
let items = [];

// API: Get all items
app.get("/items", (req, res) => {
  res.json(items);
});

// API: Add new item
app.post("/items", (req, res) => {
  items.push(req.body);
  res.json({ message: "Item added!", items });
});

// API: Delete item
app.delete("/items/:id", (req, res) => {
  const id = parseInt(req.params.id);
  items.splice(id, 1);
  res.json({ message: "Item deleted!", items });
});

// Start the server
app.listen(PORT, () => {
  console.log(`Server running at http://localhost:${PORT}`);
});

// App.js
import React, { useEffect, useState } from "react";
import { StyleSheet, Text, View, TouchableOpacity, TextInput, ScrollView, Alert } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";

/*
Expo React Native demo — National Education Management System (Android-ready)
Features:
- Multi-role login: student, teacher, admin, school
- Mock backend using AsyncStorage (seeded on first run)
- Simple dashboards and actions: verify school/teacher (admin), apply scholarship (student), upload marks/attendance (teacher), register teacher (school)
This is a demo / prototype — replace with real backend for production.
*/

const STORAGE_KEY = "nem_demo_v1";

const seedData = {
  schools: [
    { id: "SCH-001", name: "Aadi Vikas High School", verified: true },
    { id: "SCH-002", name: "Khapri Public School", verified: false }
  ],
  teachers: [
    { id: "T-101", name: "Ramesh Rao", schoolId: "SCH-001", approved: true },
    { id: "T-102", name: "Sita Sharma", schoolId: "SCH-002", approved: false }
  ],
  students: [
    { id: "S-1001", name: "Vaidhavi Sontakke", schoolId: "SCH-001", attendance: 92, scholarshipApplied: false },
    { id: "S-1002", name: "Aditya Meshram", schoolId: "SCH-001", attendance: 85, scholarshipApplied: true }
  ],
  exams: [{ id: "E-1", title: "Term 1", date: "2025-11-20", resultsPublished: false }],
  notifications: []
};

async function loadData() {
  try {
    const raw = await AsyncStorage.getItem(STORAGE_KEY);
    if (!raw) {
      await AsyncStorage.setItem(STORAGE_KEY, JSON.stringify(seedData));
      return seedData;
    }
    return JSON.parse(raw);
  } catch (e) {
    console.error("loadData error", e);
    return seedData;
  }
}
async function saveData(data) {
  try {
    await AsyncStorage.setItem(STORAGE_KEY, JSON.stringify(data));
  } catch (e) {
    console.error("saveData error", e);
  }
}

export default function App() {
  const [dataVersion, setDataVersion] = useState(0); // force refresh
  const [rolePick, setRolePick] = useState(null);
  const [current, setCurrent] = useState(null); // {role, user}
  const [data, setData] = useState(null);

  useEffect(() => {
    (async () => {
      const d = await loadData();
      setData(d);
    })();
  }, [dataVersion]);

  const reload = () => setDataVersion(v => v + 1);

  // Login logic (simple demo)
  const handleLogin = async (role, id, password) => {
    const d = await loadData();
    if (role === "student") {
      const s = d.students.find(x => x.id === id || x.name.toLowerCase() === id.toLowerCase());
      if (s) { setCurrent({ role: "student", user: s }); return; }
    }
    if (role === "teacher") {
      const t = d.teachers.find(x => x.id === id || x.name.toLowerCase() === id.toLowerCase());
      if (t) { setCurrent({ role: "teacher", user: t }); return; }
    }
    if (role === "admin") {
      // demo admin credentials
      if (id === "admin" && password === "admin123") { setCurrent({ role: "admin", user: { name: "National Admin" } }); return; }
    }
    if (role === "school") {
      const s = d.schools.find(x => x.id === id || x.name.toLowerCase() === id.toLowerCase());
      if (s) { setCurrent({ role: "school", user: s }); return; }
    }
    Alert.alert("Login failed", "Check ID / name / password (demo)");
  };

  const handleLogout = () => { setCurrent(null); setRolePick(null); reload(); };

  if (!data) {
    return (
      <View style={styles.center}>
        <Text>Loading demo data...</Text>
      </View>
    );
  }

  // ---------- Screens ----------
  if (!current) {
    return (
      <ScrollView style={styles.safe}>
        <Text style={styles.title}>National Education Management (Demo)</Text>

        <View style={styles.card}>
          <Text style={styles.small}>Choose role to login</Text>
          <View style={styles.row}>
            <TouchableOpacity style={styles.btn} onPress={() => setRolePick("student")}><Text style={styles.btnText}>Student</Text></TouchableOpacity>
            <TouchableOpacity style={styles.btn} onPress={() => setRolePick("teacher")}><Text style={styles.btnText}>Teacher</Text></TouchableOpacity>
          </View>
          <View style={styles.row}>
            <TouchableOpacity style={styles.btn} onPress={() => setRolePick("admin")}><Text style={styles.btnText}>Admin</Text></TouchableOpacity>
            <TouchableOpacity style={styles.btn} onPress={() => setRolePick("school")}><Text style={styles.btnText}>School</Text></TouchableOpacity>
          </View>
        </View>

        {rolePick && <LoginCard role={rolePick} onLogin={handleLogin} demoData={data} onBack={() => setRolePick(null)} />}

        <View style={styles.card}>
          <Text style={styles.small}>Quick demo actions</Text>
          <View style={{ flexDirection: "row", marginTop: 8 }}>
            <TouchableOpacity style={styles.btn} onPress={async () => {
              const d = await loadData();
              d.students.push({ id: `S-${Math.floor(1000 + Math.random() * 9000)}`, name: 'Demo Student ' + Date.now() % 1000, schoolId: d.schools[0].id, attendance: 80, scholarshipApplied: false });
              await saveData(d); reload(); Alert.alert("Done", "Added demo student");
            }}>
              <Text style={styles.btnText}>Add Demo Student</Text>
            </TouchableOpacity>
            <TouchableOpacity style={[styles.btn, styles.ghost]} onPress={async () => {
              await AsyncStorage.removeItem(STORAGE_KEY);
              await loadData();
              reload();
              Alert.alert("Reset", "Demo data reset");
            }}>
              <Text>Reset Data</Text>
            </TouchableOpacity>
          </View>
        </View>
      </ScrollView>
    );
  }

  // Student dashboard
  if (current.role === "student") {
    const student = data.students.find(s => s.id === current.user.id);
    const school = data.schools.find(s => s.id === student.schoolId);
    return (
      <ScrollView style={styles.safe}>
        <Text style={styles.title}>Student Dashboard</Text>
        <View style={styles.card}>
          <Text style={styles.small}>Welcome</Text>
          <Text style={styles.h3}>{student.name}</Text>
          <Text style={styles.small}>School: {school?.name || "—"}</Text>
          <Text style={{ marginTop: 8 }}>Attendance: {student.attendance}%</Text>
        </View>

        <View style={styles.card}>
          <Text style={styles.small}>Scholarship</Text>
          {student.scholarshipApplied ? <Text style={styles.notice}>You have already applied.</Text> :
            <TouchableOpacity style={styles.btn} onPress={async () => {
              const d = await loadData();
              const idx = d.students.findIndex(x => x.id === student.id);
              d.students[idx].scholarshipApplied = true;
              d.notifications.push({ id: Date.now(), text: `Scholarship applied: ${student.name}` });
              await saveData(d); reload(); Alert.alert("Applied", "Scholarship applied (demo)");
            }}>
              <Text style={styles.btnText}>Apply Scholarship</Text>
            </TouchableOpacity>}
        </View>

        <View style={styles.card}>
          <Text style={styles.small}>Upcoming Exams</Text>
          {data.exams.map(e => <View key={e.id} style={{ marginTop: 8 }}><Text>{e.title} - {e.date}</Text></View>)}
        </View>

        <TouchableOpacity style={[styles.btn, { alignSelf: "center", margin: 16 }]} onPress={handleLogout}><Text style={styles.btnText}>Logout</Text></TouchableOpacity>
      </ScrollView>
    );
  }

  // Teacher dashboard
  if (current.role === "teacher") {
    const teacher = data.teachers.find(t => t.id === current.user.id);
    return (
      <ScrollView style={styles.safe}>
        <Text style={styles.title}>Teacher Dashboard</Text>
        <View style={styles.card}>
          <Text style={styles.small}>Teacher</Text>
          <Text style={styles.h3}>{teacher.name}</Text>
          <Text style={styles.small}>School: {data.schools.find(s => s.id === teacher.schoolId)?.name}</Text>
        </View>

        <UploadMarksCard teacher={teacher} reload={reload} />

        <View style={styles.card}>
          <Text style={styles.small}>Student List (attendance quick)</Text>
          {data.students.filter(s => s.schoolId === teacher.schoolId).map(s => (
            <View key={s.id} style={styles.rowItem}>
              <View>
                <Text>{s.name}</Text>
                <Text style={styles.small}>{s.id}</Text>
              </View>
              <TouchableOpacity style={styles.ghostBtn} onPress={async () => {
                const d = await loadData();
                const idx = d.students.findIndex(x => x.id === s.id);
                d.students[idx].attendance = Math.min(100, d.students[idx].attendance + 1);
                await saveData(d); reload();
              }}>
                <Text>Mark +1%</Text>
              </TouchableOpacity>
            </View>
          ))}
        </View>

        <TouchableOpacity style={[styles.btn, { alignSelf: "center", margin: 16 }]} onPress={handleLogout}><Text style={styles.btnText}>Logout</Text></TouchableOpacity>
      </ScrollView>
    );
  }

  // Admin dashboard
  if (current.role === "admin") {
    return (
      <ScrollView style={styles.safe}>
        <Text style={styles.title}>Admin Panel</Text>
        <View style={styles.card}>
          <Text style={styles.small}>Manage schools, teachers, students and generate reports.</Text>
        </View>

        <View style={styles.card}>
          <Text style={styles.small}>Schools to verify</Text>
          {data.schools.filter(s => !s.verified).map(s => (
            <View key={s.id} style={styles.rowItem}>
              <Text>{s.name}</Text>
              <TouchableOpacity style={styles.btn} onPress={async () => {
                const d = await loadData();
                const idx = d.schools.findIndex(x => x.id === s.id);
                d.schools[idx].verified = true;
                d.notifications.push({ id: Date.now(), text: `School ${s.name} verified` });
                await saveData(d); reload();
              }}><Text style={styles.btnText}>Verify</Text></TouchableOpacity>
            </View>
          ))}
          {data.schools.filter(s => !s.verified).length === 0 && <Text style={styles.notice}>No pending schools</Text>}
        </View>

        <View style={styles.card}>
          <Text style={styles.small}>Teachers to approve</Text>
          {data.teachers.filter(t => !t.approved).map(t => (
            <View key={t.id} style={styles.rowItem}>
              <Text>{t.name}</Text>
              <TouchableOpacity style={styles.btn} onPress={async () => {
                const d = await loadData();
                const idx = d.teachers.findIndex(x => x.id === t.id);
                d.teachers[idx].approved = true;
                d.notifications.push({ id: Date.now(), text: `Teacher ${t.name} approved` });
                await saveData(d); reload();
              }}><Text style={styles.btnText}>Approve</Text></TouchableOpacity>
            </View>
          ))}
          {data.teachers.filter(t => !t.approved).length === 0 && <Text style={styles.notice}>No pending teachers</Text>}
        </View>

        <View style={styles.card}>
          <Text style={styles.small}>Reports</Text>
          <Text>Total Schools: {data.schools.length}</Text>
          <Text>Verified Schools: {data.schools.filter(s => s.verified).length}</Text>
          <Text>Total Teachers: {data.teachers.length}</Text>
          <Text>Total Students: {data.students.length}</Text>
        </View>

        <TouchableOpacity style={[styles.btn, { alignSelf: "center", margin: 16 }]} onPress={handleLogout}><Text style={styles.btnText}>Logout</Text></TouchableOpacity>
      </ScrollView>
    );
  }

  // School dashboard
  if (current.role === "school") {
    const school = data.schools.find(s => s.id === current.user.id);
    return (
      <ScrollView style={styles.safe}>
        <Text style={styles.title}>School Dashboard</Text>
        <View style={styles.card}>
          <Text style={styles.h3}>{school.name}</Text>
          <Text style={styles.small}>Verified: {school.verified ? "Yes" : "No"}</Text>
        </View>

        <View style={styles.card}>
          <Text style={styles.small}>Teachers</Text>
          {data.teachers.filter(t => t.schoolId === school.id).map(t => (
            <View key={t.id} style={styles.rowItem}>
              <View>
                <Text>{t.name}</Text>
                <Text style={styles.small}>{t.approved ? "Approved" : "Pending"}</Text>
              </View>
            </View>
          ))}
          <TouchableOpacity style={[styles.btn, { marginTop: 8 }]} onPress={async () => {
            const d = await loadData();
            const newT = { id: `T-${Math.floor(1000 + Math.random() * 9000)}`, name: `New Teacher ${Date.now() % 1000}`, schoolId: school.id, approved: false };
            d.teachers.push(newT); d.notifications.push({ id: Date.now(), text: `New teacher registered at ${school.name}` });
            await saveData(d); reload(); Alert.alert("Registered", "New teacher registered (demo)");
          }}>
            <Text style={styles.btnText}>Register Teacher (demo)</Text>
          </TouchableOpacity>
        </View>

        <TouchableOpacity style={[styles.btn, { alignSelf: "center", margin: 16 }]} onPress={handleLogout}><Text style={styles.btnText}>Logout</Text></TouchableOpacity>
      </ScrollView>
    );
  }

  return null;
}

// ---------- Small components ----------
function LoginCard({ role, onLogin, demoData, onBack }) {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  return (
    <View style={styles.card}>
      <Text style={styles.small}>Login as <Text style={{ fontWeight: "700" }}>{role}</Text></Text>
      <TextInput style={styles.input} placeholder={role === "student" ? "Student ID or name" : "ID or name"} value={id} onChangeText={setId} />
      <TextInput style={styles.input} placeholder="Password (admin: admin123)" secureTextEntry value={password} onChangeText={setPassword} />
      <View style={{ flexDirection: "row", marginTop: 8 }}>
        <TouchableOpacity style={styles.btn} onPress={() => onLogin(role, id.trim(), password)}>Login</TouchableOpacity>
        <TouchableOpacity style={[styles.btn, styles.ghost]} onPress={() => {
          // quick fill demo
          if (role === "student") setId(demoData.students[0].id);
          if (role === "teacher") setId(demoData.teachers[0].id);
          if (role === "school") setId(demoData.schools[0].id);
          if (role === "admin") { setId("admin"); setPassword("admin123"); }
        }}>Quick fill</TouchableOpacity>
      </View>
      <TouchableOpacity onPress={onBack}><Text style={{ marginTop: 8, color: "#444" }}>Back</Text></TouchableOpacity>
    </View>
  );
}

function UploadMarksCard({ teacher, reload }) {
  const [studentId, setStudentId] = useState("");
  const [marks, setMarks] = useState("");
  const upload = async () => {
    const d = await loadData();
    d.notifications.push({ id: Date.now(), text: `Marks uploaded by ${teacher.name} for ${studentId || "N/A"}` });
    await saveData(d); reload(); Alert.alert("Uploaded", "Marks uploaded (demo)");
  };
  return (
    <View style={styles.card}>
      <Text style={styles.small}>Upload Marks</Text>
      <TextInput style={styles.input} placeholder="Student ID" value={studentId} onChangeText={setStudentId} />
      <TextInput style={styles.input} placeholder="Marks" value={marks} onChangeText={setMarks} keyboardType="numeric" />
      <TouchableOpacity style={styles.btn} onPress={upload}><Text style={styles.btnText}>Upload</Text></TouchableOpacity>
    </View>
  );
}

