// server.js
import express from "express";
import multer from "multer";
import fs from "fs";
import crypto from "crypto";
import path from "path";

const app = express();
app.use(express.json());
app.use(express.static("public"));

const upload = multer({ dest: "uploads/" });
const CERT_FILE = "certificates.json";

function loadCertificates() {
  if (!fs.existsSync(CERT_FILE)) return [];
  return JSON.parse(fs.readFileSync(CERT_FILE));
}
function saveCertificates(data) {
  fs.writeFileSync(CERT_FILE, JSON.stringify(data, null, 2));
}

// Simple APIs

app.post("/api/login", (req, res) => {
  const { email, role } = req.body;
  res.json({ success: true, user: { email: email || "user", role } });
});

app.post("/api/attendance", (req, res) => {
  // Dummy attendance: always success
  res.json({ status: "Attendance Marked Successfully ✅", timestamp: new Date().toISOString() });
});

app.post("/api/issue", upload.single("pdf"), (req, res) => {
  // Accepts a 'pdf' file; computes sha256 and stores record
  let fileBuffer;
  if (req.file) {
    fileBuffer = fs.readFileSync(req.file.path);
    // cleanup upload
    try { fs.unlinkSync(req.file.path); } catch(e){}
  } else {
    // In case frontend sends a blob without file name, we may get no file
    // Fallback: create buffer from body? For demo we handle only file uploads.
    return res.status(400).json({ error: "PDF missing (send as multipart/form-data with key 'pdf')" });
  }

  const sha = crypto.createHash("sha256").update(fileBuffer).digest("hex");
  const certs = loadCertificates();
  const newCert = { id: sha, userId: req.body.userId || "student1", title: req.body.title || "Dummy Certificate", issuedAt: new Date().toISOString() };
  certs.push(newCert);
  saveCertificates(certs);

  res.json({ message: "Certificate Issued", cert: newCert, verifyUrl: `/api/verify/${sha}` });
});

app.get("/api/verify/:sha", (req, res) => {
  const sha = req.params.sha;
  const certs = loadCertificates();
  const cert = certs.find(c => c.id === sha);
  if (cert) res.json({ verified: true, cert });
  else res.json({ verified: false });
});

const PORT = process.env.PORT || 3000;
app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});
